import{_ as i,V as o,W as s,X as e,Z as n,a0 as t,$ as d,D as B,Y as l}from"./framework-a0edeef0.js";const c={},r=d('<h2 id="目录" tabindex="-1"><a class="header-anchor" href="#目录" aria-hidden="true">#</a> 目录</h2><div class="custom-container info"><p class="custom-container-title">相关信息</p><p>憨山法师生于 1546 年（明世宗嘉靖 25 年）10 月 12 日，幼年怀出家之志，11 岁时至报恩寺西林永宁法师门下，诵习佛经，兼通儒学、道家。</p><p>19 岁时谒见栖霞山云谷法会禅师，读《中峰广录》，决心参禅，返回报恩寺剃发出家。因读《华严玄谈》，崇拜清凉澄观法师为人，自字澄印。</p><p>1586 年，致信紫柏真可，两人在即墨相遇，相谈一晚，从此成为一生至交。</p><p>1590 年（万历 18 年），建成海印寺。1595 年（万历 23 年），因涉入朝廷政争，以私修庙宇之罪入狱，流放广东雷州。紫柏真可在北京奔走，企图为他平反，引起朝野议论，但真可在搭救憨山的过程，陷入第二次妖书案，遭东厂锦衣卫拷打，伤重圆寂，憨山为真可主持火化仪式。</p><p>憨山德清的全身舍利收藏在韶关曲江曹溪寺（今南华寺）。 憨山大师世寿七十八岁高龄，僧腊长达五十九年。</p><p>憨山曾自叙年谱《年谱实录》，后来经由钱谦益、吴应宾、陆梦龙等分别根据《年谱实录》撰成了《大明海印憨山大师庐山五乳峰塔铭》、《大明庐山五乳峰法云禅寺、前中兴曹溪、嗣法憨山大师塔铭有序》、《憨山大师传》等，由陆梦龙作传、吴应宾及钱谦益作塔铭。</p></div><h2 id="推荐" tabindex="-1"><a class="header-anchor" href="#推荐" aria-hidden="true">#</a> 推荐</h2>',3);function h(E,u){const a=B("RouterLink");return o(),s("div",null,[r,e("ul",null,[e("li",null,[e("p",null,[n(a,{to:"/buddhism/HanShanDeiQing/22%E5%B9%B412%E6%9C%88%E4%BB%BD/20221208.html"},{default:t(()=>[l("答郑昆岩中丞")]),_:1})])]),e("li",null,[e("p",null,[n(a,{to:"/buddhism/HanShanDeiQing/23%E5%B9%B41%E6%9C%88%E4%BB%BD/20230105-%E7%A4%BA%E5%AE%B9%E7%8E%89%E5%B1%85%E5%A3%AB(%E7%94%B2%E8%BE%B0).html"},{default:t(()=>[l("示容玉居士(甲辰)")]),_:1})])]),e("li",null,[e("p",null,[n(a,{to:"/buddhism/HanShanDeiQing/23%E5%B9%B41%E6%9C%88%E4%BB%BD/20230127-%E7%A4%BA%E6%A2%81%E4%BB%B2%E8%BF%81(%E7%94%B2%E5%AF%85).html"},{default:t(()=>[l("示梁仲迁(甲寅)")]),_:1})])])])])}const _=i(c,[["render",h],["__file","index.html.vue"]]);export{_ as default};
